import { COMPOUND_V3_ADAPTER } from "../../../../tests/constants.js";
import { CLAIM_REWARDS_SELECTOR, LEND_SELECTOR, REDEEM_SELECTOR } from "../../../constants/selectors.js";
import { decodeCallOnIntegrationArgs, encodeCallOnIntegrationArgs } from "./callOnIntegration.js";
import { type Hex, getAddress, toHex } from "viem";
import { expect, test } from "vitest";

test("encodeCallOnIntegrationArgs for Compound V3 lend should encode correctly", () => {
  expect(
    encodeCallOnIntegrationArgs({
      adapter: COMPOUND_V3_ADAPTER,
      selector: LEND_SELECTOR,
      integrationData: "0x1230",
    }),
  ).toMatchInlineSnapshot(
    '""',
  );
});

test("decodeCallOnIntegrationArgs for Compound V3 lend should decode correctly", () => {
  expect(
    decodeCallOnIntegrationArgs(
      "",
    ),
  ).toMatchInlineSnapshot(
    {
      adapter: COMPOUND_V3_ADAPTER,
      selector: LEND_SELECTOR,
      integrationData: "0x1230",
    },
    `
    {
      "adapter": "0xFaA9B9Cc98503F51A54F6038DfdD0E43AA0Ac98e",
      "integrationData": "0x1230",
      "selector": "0x099f7515",
    }
  `,
  );
});

test("decodeCallOnIntegrationArgs for Compound V3 lend should be equal to encoded data with encodeCallOnIntegrationArgs", () => {
  const params = {
    adapter: getAddress(COMPOUND_V3_ADAPTER),
    selector: LEND_SELECTOR as Hex,
    integrationData: toHex(
      "",
    ),
  };
  const encoded = encodeCallOnIntegrationArgs(params);
  const decoded = decodeCallOnIntegrationArgs(encoded);

  expect(decoded).toEqual(params);
});

