import { UNISWAP_V2_EXCHANGE_ADAPTER } from "../../../../tests/constants.js";
import { TAKE_ORDER_SELECTOR } from "../../../constants/selectors.js";
import { decodeCallOnIntegrationArgs, encodeCallOnIntegrationArgs } from "./callOnIntegration.js";
import { getAddress, toHex } from "viem";
import { expect, test } from "vitest";

test("encodeCallOnIntegrationArgs for Uniswap V2 Exchange take order should encode correctly", () => {
  expect(
    encodeCallOnIntegrationArgs({
      adapter: UNISWAP_V2_EXCHANGE_ADAPTER,
      selector: TAKE_ORDER_SELECTOR,
      integrationData: "0x1230",
    }),
  ).toMatchInlineSnapshot(
    '""',
  );
});

test("decodeCallOnIntegrationArgs for Uniswap V2 Exchange take order should decode correctly", () => {
  expect(
    decodeCallOnIntegrationArgs(
      "",
    ),
  ).toMatchInlineSnapshot(
    {
      adapter: UNISWAP_V2_EXCHANGE_ADAPTER,
      selector: TAKE_ORDER_SELECTOR,
      integrationData: "0x1230",
    },
    `
    {
      "adapter": "0x8c36435A653041BFd65515CC82502663C1ce6F0e",
      "integrationData": "0x1230",
      "selector": "0x03e38a2b",
    }
  `,
  );
});

test("decodeCallOnIntegrationArgs for Uniswap V2 Exchange take order should be equal to encoded data with encodeCallOnIntegrationArgs", () => {
  const params = {
    adapter: getAddress(UNISWAP_V2_EXCHANGE_ADAPTER),
    selector: TAKE_ORDER_SELECTOR,
    integrationData: toHex(
      "",
    ),
  };
  const encoded = encodeCallOnIntegrationArgs(params);
  const decoded = decodeCallOnIntegrationArgs(encoded);

  expect(decoded).toEqual(params);
});
